const TodoItemBlock = styled.div`
    display : flex;
    align-item : center;
    padding-top : 12px; 
    padding-bottom : 12px;
    &:hover {
    } 
`;
const Text = styled.div`
    font-size : 21px; 
    color: #495057;
    ${props => props.done &&  css`color:#ced4da;`}        
    }   
`;
const CheckCircle = styled.div`

`;
function TodoItem({id, done, text}){

    return (
        <TodoItemBlock>
            <CheckCircle done={done}></CheckCircle>
            <Text done={done}>{text}</Text>
        </TodoItemBlock>
    );
};
